{
  "name": "node-hello-world",
  "version": "1.0.0",
  "description": "A simple Node.js hello world web server",
  "main": "server.js",
  "scripts": {
    "start": "node server.js",
    "test": "node server.js & pid=$!; sleep 1; curl -s http://localhost:3000; kill $pid"
  },
  "dependencies": {}
}

const http = require('http');

const server = http.createServer((req, res) => {
  res.statusCode = 200;
  res.setHeader('Content-Type', 'text/plain');
  res.end('Hello, World!');
});

const port = 3000;
server.listen(port, () => {
  console.log(`Server running at http://localhost:${port}/`);
});
