{
  "name": "gantry-landing-page",
  "version": "1.0.0",
  "description": "Simple landing page for Gantry",
  "main": "server.js",
  "scripts": {
    "start": "node server.js",
    "test": "echo \"No tests specified\" && exit 0"
  },
  "dependencies": {
    "express": "^4.17.1"
  }
}

const express = require('express');
const app = express();
const port = 3000;

app.get('/', (req, res) => {
  res.send('<h1>Welcome to Gantry</h1>');
});

app.listen(port, () => {
  console.log(`Server running at http://localhost:${port}`);
});
